import { styled } from "styled-components";

const Wrapper=styled.div`
div {
    text-align: center;
    padding: 20px;
    background-color: #007bff;
    color: #fff;
    border-bottom: 4px solid #0056b3;
    border-radius: 0 0 8px 8px;
  }
  
  h1 {
    font-size: 24px;
    margin: 0;
    padding: 10px 0;
  }
  

`
export default Wrapper;
