import {styled} from "styled-components"
const Wrapper=styled.main`
.main {
    text-align: center;
    margin: 20px;
  }
  
  h1 {
    font-size: 24px;
    margin-bottom: 20px;
  }
  
  .button.inner {
    margin: 10px;
    
    input[type="button"] {
      background-color: #3498db;
      color: white;
      padding: 8px 16px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      margin-right: 10px;
      
      &:hover {
        background-color: #2980b9;
      }
    }
  }
  
  .studentYear.inner,
  .studentSection.inner {
    margin: 10px;
  
    label {
      display: block;
      font-weight: bold;
      margin-bottom: 5px;
    }
  
    select {
      padding: 8px;
      border: 1px solid #ccc;
      border-radius: 4px;
      width: 100%;
    }
  }
`
export default Wrapper;
