import {styled} from "styled-components"
const Wrapper=styled.div`
margin:auto;
max-width:80%;

div{
    display:flex;
    gap:20px
}
h1{
    font-size:50px;
    margin:auto;
    max-width:45%;
}
input{
    font-size:20px;
}
`
export default Wrapper;
