import {styled} from "styled-components"
const Wrapper=styled.div`
margin:auto;
max-width:80%;

div{
    display:flex;
    gap:20px;
    margin:auto;
    max-width:15%;
    margin-top:20px;
    
}
h1{
    font-size:50px;
    margin:auto;
    max-width:45%;
}
input{
    font-size:20px;
    background-color:black;
    color:white;
    padding:2px 10px ;
    &:hover{
        background-color:white;
    color:black;
    }
}
`
export default Wrapper;
