import {styled} from "styled-components"
const Wrapper=styled.div`
    display:flex;
    gap:20px;
    margin:auto;
    width:90%;
    justify-content:center;

h1{
    font-size:50px;
    
}
input{
    font-size:20px;
    background-color:black;
    color:white;
    padding:4px 15px;
    &:hover{
        background-color:white;
    color:black;
    }

}
`
export default Wrapper;
