import {styled} from "styled-components"
const Wrapper=styled.main`
main
{
    display: flex;
    align-items: center;
    height:100vh;
    justify-content: center;
}
div{
    label
    {
        margin-right:5px;
    }
}
h1{
    font-size: 1.8rem;
}
.button
{
    
    display :flex;
    justify-content :space-around;
    input{
        width: 35%;
        font-size:1rem;
        &:hover{
            cursor:pointer;
        }
        }
}
.inner{
    padding: 10px 15px;
    margin: 15px 10px;
}
`
export default Wrapper;
