import { styled } from "styled-components";

const Wrapper=styled.div`

ul{
    background-color:black;
    display:flex;
    list-style:none;
    height:30px;
    justify-content:space-around;
    padding-bottom:5px;
    align-items:center;
    li{
        background-color:black;
        a{
            text-decoration:none;
            font-size:20px;
            color:white;
            background-color:black;
            &:hover{
                color:rgb(97, 280, 240);
                text-decoration:underline;
                
            }
        }
       
        
    }
}
`
export default Wrapper;
