import {styled} from "styled-components"
const Wrapper=styled.main`



// Style for the main container
main {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh; /* Centers the content vertically */
  background: linear-gradient(45deg,
    lavenderblush,purple);
}

// Style for the title
h1 {
  font-size: 24px;
  margin-bottom: 20px;
}

// Style for the button container
.button.inner {
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
}

// Style for buttons
input[type="button"] {
  padding: 10px 20px;
  background-color: #007bff;
  color: #fff;
  border: none;
  cursor: pointer;
}

// Style for select dropdowns
.studentYear.inner,
.studentSection.inner {
  margin-bottom: 20px;
}

select {
  padding: 8px;
  border: 1px solid #ccc;
  border-radius: 4px;
}

// Style for labels
label {
  font-weight: bold;
  margin-right: 10px;
}

`
export default Wrapper;
