import {styled} from "styled-components"
const Wrapper=styled.main`
display: flex;
align-items: center;
margin: 20px 50px;
justify-content: center;

h1{
    font-size: 3rem;
}
.button
{
    padding :20px 10px;
    display :flex;
    justify-content :space-around;
    input{
        width: 35%;
        }
}
`
export default Wrapper;
