import {styled} from "styled-components"
const Wrapper=styled.main`
display: flex;
align-items: center;
margin: 20px 50px;
justify-content: center;

h1{
    font-size: 1.8rem;
}
.button
{
    
    display :flex;
    justify-content :space-around;
    input{
        width: 35%;
        font-size:1rem;
        &:hover{
            cursor:pointer;
        }
        }
}
.inner{
    padding :10px 10px;
}
`
export default Wrapper;
