import {styled} from "styled-components"
const Wrapper=styled.main`
// Styles.scss

.main {
  font-family: Arial, sans-serif;
  text-align: center;
  padding: 20px;
}

h1 {
  font-size: 24px;
  margin-bottom: 20px;
}

.button {
  &.inner {
    input[type="button"] {
      background-color: #007bff;
      color: #fff;
      border: none;
      padding: 10px 20px;
      margin: 5px;
      cursor: pointer;
      border-radius: 5px;
      transition: background-color 0.3s ease;

      &:hover {
        background-color: #0056b3;
      }
    }
  }
}

.studentYear,
.studentSection {
  &.inner {
    label {
      display: block;
      font-size: 16px;
      margin-bottom: 5px;
    }

    select {
      width: 100%;
      padding: 10px;
      border: 1px solid #ccc;
      border-radius: 5px;
      font-size: 14px;
    }
  }
}
`
export default Wrapper;
